/* * 作成日: 2003/12/18 * */package jp.ac.titech.is.wakitalab.color.shimamura;/** * @author shimaken * */public class IntVector extends java.util.Vector<Integer> {		public IntVector() {		super();	}		public void add (int index, int n) {		Integer integer = new Integer(n);		super.add(index, integer);	}		public void add (int n) {		Integer integer = new Integer(n);		super.add(integer);	}			public int indexOf(int n) {		Integer integer = new Integer(n);		return super.indexOf(integer);	}		public boolean removeInt(int n) {		Integer integer = new Integer(n);		return super.remove(integer);	}		public Object set(int index, int n) {		Integer integer = new Integer(n);		return super.set(index, integer);	}}